#include "xq/net/rux_client.hpp"

//#define SERVER_ENDPOINT ("192.168.0.104:6688")
//#define SERVER_ENDPOINT ("127.0.0.1:6688")
#define SERVER_ENDPOINT ("1.15.81.179:6688")
#define LIMIT (1000)


class EchoService;
static xq::net::Udx<xq::net::RuxClient<EchoService>>::ptr pUdx;

class EchoService {
public:
    void on_message(xq::net::Rux::ptr rux, uint8_t* msg, int msglen) {
        static int count = 0;

        msg[msglen] = 0;
        //DLOG("recv: %s\n", (char*)msg);
        if (++count == LIMIT) {
            // pUdx->stop();
        }
    }
};


int 
main(int argc, char** argv) {
    char* buf = new char[xq::net::RUX_MSG_MAX]{};
    ASSERT(!rux_env_init());

    EchoService es;
    xq::net::RuxClient client(1, &es);
    xq::net::Udx udx(":0", &client);
    pUdx = &udx;

    client.connect_node(SERVER_ENDPOINT, sys_time(), udx.snd_que());
    udx.run();

    int64_t beg = sys_time();
    for (int i = 0; i < LIMIT; i++) {
        sprintf(buf, ": %d", i + 1);;
        client.send(SERVER_ENDPOINT, (uint8_t*)buf, strlen(buf));
    }

    udx.wait();
    DLOG("exit: %lld s !!!", (sys_time() - beg) / 1000000);
    ASSERT(!rux_env_release());

    delete[] buf;
    exit(EXIT_SUCCESS);
}